import React from 'react';
import { Container, Typography, Grid, Card, CardContent, CardMedia } from '@mui/material';
import { styled } from '@mui/material/styles';

const menuItems = [
  { title: 'Multiple Choice', image: 'MultıpleChoıce.png' },
  { title: 'Sätze Bilden', image: 'SatzeBilden.png' },
  { title: 'Lernen mit Musik', image: 'Lernenmıtmusık.png' },
  { title: 'Leseverstehen', image: 'LESEVERSTEHEN.png' },
  { title: 'Wortschatz', image: 'WORTSCHATZ.png' },
  { title: 'Zuordnung', image: 'ZUORDNUNG-2.png' },
  { title: 'Grammatik', image: 'GRAMMER.png' },
  { title: 'Frage & Antwort', image: 'Frage-Antwort.png' },
  { title: 'DeutschApp', image: 'deutschapp.png' }
];

const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.2s',
  backgroundColor: '#FFF8E1',
  '&:hover': {
    transform: 'scale(1.05)',
    boxShadow: '0 8px 16px rgba(255, 152, 0, 0.2)',
  },
}));

const StyledCardMedia = styled(CardMedia)({
  height: 180,
  width: '100%',
  backgroundSize: 'contain',
  backgroundPosition: 'center',
  backgroundColor: 'white',
  padding: '15px',
  margin: '10px',
  borderRadius: '8px',
  objectFit: 'contain',
  '& img': {
    maxHeight: '100%',
    maxWidth: '100%',
    objectFit: 'contain',
  }
});

function App() {
  return (
    <Container maxWidth="lg" sx={{ py: 4, backgroundColor: '#FFF3E0' }}>
      <Typography 
        variant="h4" 
        component="h1" 
        sx={{ 
          mb: 4, 
          fontWeight: 'bold',
          color: '#E65100',
          textAlign: 'center',
          textShadow: '2px 2px 4px rgba(0,0,0,0.1)'
        }}
      >
        DeutschApp
      </Typography>
      <Grid container spacing={3}>
        {menuItems.map((item, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <StyledCard>
              <StyledCardMedia
                component="img"
                image={process.env.PUBLIC_URL + '/' + item.image}
                title={item.title}
                alt={item.title}
              />
              <CardContent sx={{ textAlign: 'center', padding: '12px' }}>
                <Typography 
                  variant="h6" 
                  component="h2"
                  sx={{ 
                    color: '#E65100',
                    fontWeight: 'medium',
                    fontSize: '1.1rem'
                  }}
                >
                  {item.title}
                </Typography>
              </CardContent>
            </StyledCard>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default App;
