import React from 'react';
import { Box, Container, Typography, Grid, Card, CardContent, CardMedia } from '@mui/material';
import { styled } from '@mui/material/styles';

const menuItems = [
  { title: 'Multiple Choice', image: 'MultıpleChoıce.png' },
  { title: 'Sätze Bilden', image: 'SatzeBilden.png' },
  { title: 'Lernen mit Musik', image: 'Lernenmıtmusık.png' },
  { title: 'Leseverstehen', image: 'LESEVERSTEHEN.png' },
  { title: 'Wortschatz', image: 'WORTSCHATZ.png' },
  { title: 'Zuordnung', image: 'ZUORDNUNG-2.png' },
  { title: 'Grammatik', image: 'GRAMMER.png' },
  { title: 'Frage & Antwort', image: 'Frage-Antwort.png' },
  { title: 'DeutschApp', image: 'deutschapp.png' }
];

const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.2s',
  '&:hover': {
    transform: 'scale(1.05)',
    boxShadow: '0 8px 16px rgba(0,0,0,0.2)',
  },
}));

const StyledCardMedia = styled(CardMedia)({
  height: 200,
  backgroundSize: 'contain',
  backgroundPosition: 'center',
  backgroundColor: 'white',
  padding: '20px',
});

function App() {
  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" sx={{ mb: 4, fontWeight: 'bold' }}>
        DeutschApp
      </Typography>
      <Grid container spacing={3}>
        {menuItems.map((item, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <StyledCard>
              <StyledCardMedia
                image={item.image}
                title={item.title}
              />
              <CardContent sx={{ textAlign: 'center' }}>
                <Typography variant="h6" component="h2">
                  {item.title}
                </Typography>
              </CardContent>
            </StyledCard>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default App;
