];

export default function MultipleChoice() {
  const [selected, setSelected] = useState(Array(questions.length).fill(null));
  const [showResult, setShowResult] = useState(false);

  const handleChange = (qIdx, value) => {
    const updated = [...selected];
    updated[qIdx] = value;
    setSelected(updated);
  };

  const handleSubmit = () => {
    setShowResult(true);
  };

  const correctCount = selected.filter((val, idx) => parseInt(val) === questions[idx].answer).length;

  return (
    <Box sx={{ maxWidth: 700, mx: 'auto', mt: 4 }}>
      <Typography variant="h5" sx={{ mb: 2, color: '#E65100', fontWeight: 'bold' }}>
        A1 Was ist richtig? Kreuzen Sie an.
      </Typography>
      {questions.map((q, idx) => (
        <Paper key={idx} sx={{ p: 2, mb: 2, background: '#FFF8E1' }}>
          <Typography sx={{ mb: 1 }}>{idx + 1}. {q.question}</Typography>
          <RadioGroup
            value={selected[idx] ?? ''}
            onChange={e => handleChange(idx, e.target.value)}
          >
            {q.options.map((opt, oIdx) => (
              <FormControlLabel
                key={oIdx}
                value={oIdx.toString()}
                control={<Radio color="primary" />}
                label={opt}
                disabled={showResult}
                sx={{
                  color: showResult && q.answer === oIdx ? 'green' : undefined,
                  fontWeight: showResult && q.answer === oIdx ? 'bold' : undefined
                }}
              />
            ))}
          </RadioGroup>
          {showResult && selected[idx] !== null && (
            <Typography sx={{ color: selected[idx] === q.answer ? 'green' : 'red', mt: 1 }}>
              {selected[idx] === q.answer ? 'Doğru!' : `Yanlış! Doğru cevap: ${q.options[q.answer]}`}
            </Typography>
          )}
        </Paper>
      ))}
      {!showResult && (
        <Button variant="contained" color="primary" onClick={handleSubmit} sx={{ mt: 2 }}>
          Cevapları Göster
        </Button>
      )}
      {showResult && (
        <Typography sx={{ mt: 3, fontWeight: 'bold', color: '#E65100' }}>
          Doğru sayısı: {correctCount} / {questions.length}
        </Typography>
      )}
    </Box>
  );
}
